import React, { useState } from 'react';
import { Link } from "react-router-dom";
import testerImg from '../img/testerimg.png';
import projects from './projectsData';

function Project({title, description, image, stack, type}) {

    const typeBubbleClassName = `type-bubble-text ${type === 'design' ? 'design' : 'dev'}`;
    const projectPageName = title.toLowerCase().replace(/\s+/g, '-');
    const projectPage = `/project/${projectPageName}`;

    const [showMessage, setShowMessage] = useState(false);

    const handleMouseOver = () => {
      setShowMessage(true);
    };

    const handleMouseOut = () => {
      setShowMessage(false);
    };

    return (
        <div className="project">
            <Link to={projectPage}>
                <div className="project-tile" onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
                {showMessage && <div className="message"><p>Click to see more</p></div>}
                    <img className="project-img" src={image}/>
                </div>
            </Link>
            <div class="project-text">
                <h5 className="project-type">{type}</h5>
                <h3>{title}</h3>
                <h4>{stack}</h4>
                {/* <p>{description}</p> */}
            </div>
        </div>
    );
}


function Dev() {
    return (
        <section className="dev">
            {projects.map((project, index) => (
                <Project key={index} {...project} />
            ))}
        </section>

    );
}

export default Dev;
